package com.github.studydistractor.sdp.viewModels

import androidx.compose.ui.test.assertHasClickAction
import androidx.compose.ui.test.assertIsDisplayed
import androidx.compose.ui.test.junit4.createComposeRule
import androidx.compose.ui.test.onNodeWithTag
import androidx.compose.ui.test.performClick
import androidx.compose.ui.test.performTextInput
import com.github.studydistractor.sdp.createUser.CreateUserViewModel
import com.github.studydistractor.sdp.fakeServices.CreateUserServiceFake
import com.github.studydistractor.sdp.ui.CreateUserScreen
import com.google.android.gms.tasks.Tasks
import org.junit.Assert
import org.junit.Before
import org.junit.Rule
import org.junit.Test
import java.text.SimpleDateFormat
import java.util.concurrent.ExecutionException
import java.util.concurrent.TimeUnit

class CreateUserViewModelTest {
    val createUserViewModel = CreateUserViewModel(CreateUserServiceFake())

    /**
     * Tasks.await() throws an exception on failure
     */
    @Test
    fun testEmptyUserId(){
        Assert.assertThrows(ExecutionException::class.java){
            Tasks.await(createUserViewModel.createUser(), 100, TimeUnit.MILLISECONDS)
        }
    }

    @Test
    fun testInvalidBirthdayFormat(){
        createUserViewModel.updateFirstname("firstname")
        createUserViewModel.updateLastname("lastname")
        createUserViewModel.updatePhoneNumber("+41000000000")
        Assert.assertThrows(ExecutionException::class.java) {
            createUserViewModel.updateDateText("blablabla")
            Tasks.await(createUserViewModel.createUser(), 100, TimeUnit.MILLISECONDS)
        }
    }

    @Test
    fun testInvalidFirstName(){
        val firstname1 = ""
        val firstname2 = ""

        Assert.assertThrows(ExecutionException::class.java) {
            createUserViewModel.updateFirstname(firstname1)
            Tasks.await(createUserViewModel.createUser(), 100, TimeUnit.MILLISECONDS)
        }

        Assert.assertThrows(ExecutionException::class.java) {
            createUserViewModel.updateFirstname(firstname2)
            Tasks.await(createUserViewModel.createUser(), 100, TimeUnit.MILLISECONDS)
        }
    }

    @Test
    fun testInvalidLastName(){
        createUserViewModel.updateFirstname("firstname")

        val firstname = "firstname"
        val lastname1 = ""
        val lastname2 = ""


        Assert.assertThrows(ExecutionException::class.java) {
            createUserViewModel.updateLastname(lastname1)
            Tasks.await(createUserViewModel.createUser(), 100, TimeUnit.MILLISECONDS)
        }

        Assert.assertThrows(ExecutionException::class.java) {
            createUserViewModel.updateLastname(lastname2)
            Tasks.await(createUserViewModel.createUser(), 100, TimeUnit.MILLISECONDS)
        }
    }

    @Test
    fun testInvalidPhoneFormat(){
        createUserViewModel.updateFirstname("firstname")
        createUserViewModel.updateLastname("lastname")
        val userId = "userId"
        val birthday =0

        val firstname = "firstname"
        val lastname = "lastname"
        val phone1 = ""
        val phone2 = "s111"
        val phone3 = "+550000000000ff000000222232323232323232323232323232323"

        Assert.assertThrows(ExecutionException::class.java) {
            createUserViewModel.updatePhoneNumber(phone1)
            Tasks.await(createUserViewModel.createUser(), 100, TimeUnit.MILLISECONDS)
        }
        Assert.assertThrows(ExecutionException::class.java) {
            createUserViewModel.updatePhoneNumber(phone2)
            Tasks.await(createUserViewModel.createUser(), 100, java.util.concurrent.TimeUnit.MILLISECONDS)
        }
        Assert.assertThrows(ExecutionException::class.java) {
            createUserViewModel.updatePhoneNumber(phone3)
            Tasks.await(createUserViewModel.createUser(), 100, java.util.concurrent.TimeUnit.MILLISECONDS)
        }
    }

    @Test
    fun testCreateValidAndGetUser(){
        val dateTest = "1970-01-01"
        createUserViewModel.updateFirstname("firstname")
        createUserViewModel.updateLastname("lastname")
        createUserViewModel.updatePhoneNumber("+41000000000")
        createUserViewModel.updateDateText(dateTest)


        val birthday = (SimpleDateFormat("yyyy-mm-dd").parse(dateTest).time/1000L).toInt()

        // If it throws an exception then it should stop the test
        Tasks.await(createUserViewModel.createUser(), 100, TimeUnit.MILLISECONDS)

    }
}
class CreateUserScreenTest {

    @get:Rule(order = 1)
    val composeRule = createComposeRule()

    @Before
    fun setup() {
        composeRule.setContent {
            CreateUserScreen({},CreateUserViewModel(CreateUserServiceFake()))
        }
    }
    @Test
    fun testUiIsDisplay(){
        composeRule.onNodeWithTag("create-account-screen").assertIsDisplayed()
        composeRule.onNodeWithTag("create-account-screen__firstname").assertIsDisplayed()
        composeRule.onNodeWithTag("create-account-screen__firstname").performTextInput("firstname")

        composeRule.onNodeWithTag("create-account-screen__lastname").assertIsDisplayed()
        composeRule.onNodeWithTag("create-account-screen__lastname").performTextInput("lastname")

        composeRule.onNodeWithTag("create-account-screen__birthday").assertIsDisplayed()
        composeRule.onNodeWithTag("create-account-screen__birthday").performTextInput("1970-01-01")

        composeRule.onNodeWithTag("create-account-screen__selectBirthdayButton").assertIsDisplayed()
        composeRule.onNodeWithTag("create-account-screen__selectBirthdayButton").assertHasClickAction()
        composeRule.onNodeWithTag("create-account-screen__selectBirthdayButton").performClick()


        composeRule.onNodeWithTag("create-account-screen__phone").assertIsDisplayed()
        composeRule.onNodeWithTag("create-account-screen__phone").performTextInput("+41000000000")
        composeRule.onNodeWithTag("create-account-screen__validbutton").assertIsDisplayed()
        composeRule.onNodeWithTag("create-account-screen__validbutton").assertHasClickAction()
        composeRule.onNodeWithTag("create-account-screen__validbutton").performClick()

    }

}