package com.github.studydistractor.sdp.user

import androidx.compose.ui.platform.LocalContext
import org.hamcrest.MatcherAssert
import org.hamcrest.Matchers
import org.junit.Assert
import org.junit.Test

class UserViewModelTest {
    val userService = FakeCreateUserImplementation()
    @Test
    fun testNullUser(){
        Assert.assertThrows(IllegalArgumentException::class.java) {
            val current = LocalContext
            UserViewModel().processUser(null, userService)
        }

    }
    @Test
    fun testEmptyUserId(){
        Assert.assertThrows(IllegalArgumentException::class.java) {
            UserViewModel().processUser(UserData("", "", "", "", hashMapOf(), 0), userService)
        }
    }

    @Test
    fun testInvalidBirthdayFormat(){
        val birthday1 = hashMapOf<String, Int>()
        val birthday2 = hashMapOf<String, Int>(
            "test" to 1,
            "month" to 1,
            "day" to 1
        )
        val birthday3 = hashMapOf<String, Int>(
            "test" to 1,
            "year" to 1,
            "day" to 1
        )
        val birthday4 = hashMapOf<String, Int>(
            "year" to 1,
            "month" to 1,
            "test" to 1
        )
        val birthday5 = hashMapOf<String, Int>(
            "year" to 0,
            "month" to 0,
            "day" to 0
        )
        val birthday6 = hashMapOf<String, Int>(
            "year" to 0,
            "month" to 0,
            "day" to 1
        )
        val birthday7 = hashMapOf<String, Int>(
            "year" to 0,
            "month" to 1,
            "day" to 1
        )

        Assert.assertThrows(IllegalArgumentException::class.java) {
            UserViewModel().processUser(UserData("userid", "", "", "", birthday1, 0), userService)
        }

        Assert.assertThrows(IllegalArgumentException::class.java) {
            UserViewModel().processUser(UserData("userid", "", "", "", birthday2, 0), userService)
        }

        Assert.assertThrows(IllegalArgumentException::class.java) {
            UserViewModel().processUser(UserData("userid", "", "", "", birthday3, 0), userService)
        }
        Assert.assertThrows(IllegalArgumentException::class.java) {
            UserViewModel().processUser(UserData("userid", "", "", "", birthday4, 0), userService)
        }

        Assert.assertThrows(IllegalArgumentException::class.java) {
            UserViewModel().processUser(UserData("userid", "", "", "", birthday5, 0), userService)
        }

        Assert.assertThrows(IllegalArgumentException::class.java) {
            UserViewModel().processUser(UserData("userid", "", "", "", birthday6, 0), userService)
        }

        Assert.assertThrows(IllegalArgumentException::class.java) {
            UserViewModel().processUser(UserData("userid", "", "", "", birthday7, 0), userService)
        }
    }

    @Test
    fun testInvalidFirstName(){
        val userId = "userId"
        val birthday = hashMapOf<String,Int>(
            "year" to 1,
            "month" to 1,
            "day" to 1
        )

        val firstname1 = ""
        val firstname2 = ""
        val length = firstname2.length

        MatcherAssert.assertThat(
            "testlength",
            firstname2.length,
            Matchers.greaterThan(255)
        )
        Assert.assertThrows(IllegalArgumentException::class.java) {
            UserViewModel().processUser(UserData(userId, firstname1, "", "", birthday, 0), userService)
        }
        Assert.assertThrows(IllegalArgumentException::class.java) {
            UserViewModel().processUser(UserData(userId, firstname2, "", "", birthday, 0), userService)
        }
    }

    @Test
    fun testInvalidLastName(){
        val userId = "userId"
        val birthday = hashMapOf<String,Int>(
            "year" to 1,
            "month" to 1,
            "day" to 1
        )

        val firstname = "firstname"
        val lastname1 = ""
        val lastname2 = ""

        MatcherAssert.assertThat(
            "testlength",
            lastname2.length,
            Matchers.greaterThan(255)
        )
        Assert.assertThrows(IllegalArgumentException::class.java) {
            UserViewModel().processUser(UserData(userId, firstname, lastname1, "", birthday, 0), userService)
        }
        Assert.assertThrows(IllegalArgumentException::class.java) {
            UserViewModel().processUser(UserData(userId, firstname, lastname2, "", birthday, 0), userService)
        }
    }

    @Test
    fun testInvalidPhoneFormat(){
        val userId = "userId"
        val birthday = hashMapOf<String,Int>(
            "year" to 1,
            "month" to 1,
            "day" to 1
        )

        val firstname = "firstname"
        val lastname = "lastname"
        val phone1 = ""
        val phone2 = "s111"
        val phone3 = "+550000000000ff000000222232323232323232323232323232323"

        Assert.assertThrows(IllegalArgumentException::class.java) {
            UserViewModel().processUser(UserData(userId, firstname, lastname, phone1, birthday, 0), userService)
        }
        Assert.assertThrows(IllegalArgumentException::class.java) {
            UserViewModel().processUser(UserData(userId, firstname, lastname, phone2, birthday,0 ), userService)
        }
        Assert.assertThrows(IllegalArgumentException::class.java) {
            UserViewModel().processUser(UserData(userId, firstname, lastname, phone3, birthday, 0), userService)
        }
    }

    @Test
    fun testCreateValidUser(){
        val userId = "userId"
        val birthday = hashMapOf<String,Int>(
            "year" to 1,
            "month" to 1,
            "day" to 1
        )
        val firstname = "firstname"
        val lastname = "lastname"
        val phone = "+41000000000"

        var usr = UserData(userId,firstname,lastname,phone, birthday, 0)
        UserViewModel().processUser(usr, userService)
        Assert.assertEquals(usr.id, userId)
        Assert.assertEquals(usr.score, 0)
        Assert.assertEquals("${usr.firstname} ${usr.lastname}", "$firstname $lastname")
        Assert.assertEquals(usr.phone, phone)
        for (key in usr.birthday!!.keys){
            Assert.assertEquals(birthday[key], usr.birthday!![key])
        }

    }
}